import React, { useEffect, useState } from 'react';
import { Container, Typography, Box, Grid, List, ListItem, ListItemText, Paper } from '@mui/material';
import aboutBanner from '../../assets/about_banner.jpg';
import directorPhoto from '../../assets/director.jpeg'; // 👈 원장님 사진
//import mapImage from '../../assets/map.jpg'; // 👈 오시는길 지도 이미지 (또는 주소)
import { supabase } from '../../lib/supabaseClient';

function About() {
 const [historyList, setHistoryList] = useState([]);
 const fetchHistory = async () => {
  const { data, error } = await supabase.from('history').select('*').order('id', { ascending: false });
  if (!error) setHistoryList(data);
 };
 useEffect(() => {
   fetchHistory();
 }, []);

  return (
    <div>
      <img src={aboutBanner} alt="Artful Kids Banner" style={{ width: '100%' }} />

      <Container sx={{ mt: 4 }}>
        {/* 🎨 학원 소개 */}
        <Typography variant="body1" color="textSecondary" paragraph>
          Artful은 창의적인 교육 방식을 통해 아이들의 자율성과 감각을 키워가는 예술 학원입니다.
        </Typography>

        {/* 👩‍🏫 원장님 경력 */}
        <Box sx={{ mt: 6 }}>
          <Typography variant="h5" fontWeight="bold" gutterBottom>
            원장님 소개
          </Typography>

          <Grid container spacing={3} alignItems="center">
           {/* 이미지 영역 */}
           <Grid item xs={12} md={6}>
             <img
               src={directorPhoto}
               alt="원장님 사진"
               style={{ width: '100%', display: 'block', borderRadius: '12px' }}
             />
           </Grid>

           {/* 텍스트 리스트 영역 */}
           <Grid item xs={12} md={6}>
             <List>
               {historyList.map((item) => (
                 <ListItem key={item.id}>
                   <ListItemText primary={item.info} />
                 </ListItem>
               ))}
             </List>
           </Grid>
         </Grid>
        </Box>

        {/* 📍 오시는 길 */}
        <Box sx={{ mt: 6 }}>
          <Typography variant="h5" fontWeight="bold" gutterBottom>
            오시는 길
          </Typography>

          <Paper elevation={2} sx={{ p: 2, mt: 2 }}>
            <Grid container spacing={3}>
              <Grid item xs={12} md={6}>
                {/* <img src={mapImage} alt="지도 이미지" style={{ width: '100%', borderRadius: '8px' }} /> */}
              </Grid>
              <Grid item xs={12} md={6}>
                <Typography variant="body1" paragraph>
                  <strong>주소:</strong> 서울특별시 강남구 테헤란로 123, 3층
                </Typography>
                <Typography variant="body1" paragraph>
                  <strong>지하철:</strong> 2호선 강남역 3번 출구 도보 5분
                </Typography>
                <Typography variant="body1">
                  <strong>전화:</strong> 02-1234-5678
                </Typography>
              </Grid>
            </Grid>
          </Paper>
        </Box>
      </Container>
    </div>
  );
}

export default About;
