// ProgramDialog.jsx
import React, { useEffect } from 'react';
import {
  Dialog, DialogTitle, DialogContent, DialogActions,
  TextField, Button
} from '@mui/material';
import { useEditor, EditorContent } from '@tiptap/react';
import StarterKit from '@tiptap/starter-kit';

function ProgramDialog({ open, handleClose, handleSave, title, setTitle, content, setContent }) {
  const editor = useEditor({
    extensions: [StarterKit],
    content: content || '',
    onUpdate({ editor }) {
      setContent(editor.getHTML());
    },
  });

  useEffect(() => {
    if (editor && open) {
      editor.commands.setContent(content || '');
    }
  }, [editor, content, open]);

  return (
    <Dialog open={open} onClose={handleClose} maxWidth="md" fullWidth>
      <DialogTitle>프로그램 {title ? '수정' : '추가'}</DialogTitle>
      <DialogContent dividers>
        <TextField
          fullWidth
          label="제목"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          margin="normal"
        />
        <div style={{ border: '1px solid #ccc', borderRadius: '6px', padding: '12px', minHeight: '200px' }}>
          {editor && <EditorContent editor={editor} />}
        </div>
      </DialogContent>
      <DialogActions>
        <Button variant="contained" onClick={handleSave}>저장</Button>
        <Button onClick={handleClose}>취소</Button>
      </DialogActions>
    </Dialog>
  );
}

export default ProgramDialog;
