import React, { useEffect, useState } from 'react';
import {
  Box, Button, Dialog, DialogActions, DialogContent, DialogTitle,
  TextField, Typography, Table, TableBody, TableCell, TableHead, TableRow,
  IconButton, Paper
} from '@mui/material';
import { Add, Edit, Delete } from '@mui/icons-material';
import { supabase } from '../../lib/supabaseClient';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';

function Program() {
  const [programs, setPrograms] = useState([]);
  const [open, setOpen] = useState(false);
  const [editItem, setEditItem] = useState(null);
  const [title, setTitle] = useState('');
  const [info, setInfo] = useState('');

  const fetchPrograms = async () => {
    const { data, error } = await supabase.from('program').select('*').order('id', { ascending: true });
    if (!error) setPrograms(data);
  };

  useEffect(() => {
    fetchPrograms();
  }, []);

  const handleOpen = (item = null) => {
    setEditItem(item);
    setTitle(item ? item.title : '');
    setInfo(item ? item.info : '');
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    setEditItem(null);
    setTitle('');
    setInfo('');
  };

  const handleSave = async () => {
    if (!title.trim()) {
      alert('제목을 입력하세요.');
      return;
    }

    if (editItem) {
      await supabase.from('program').update({ title, info }).eq('id', editItem.id);
    } else {
      await supabase.from('program').insert({ title, info });
    }

    handleClose();
    fetchPrograms();
  };

  const handleDelete = async (id) => {
    if (window.confirm('정말 삭제하시겠습니까?')) {
      await supabase.from('program').delete().eq('id', id);
      fetchPrograms();
    }
  };

  return (
    <Box>
      <Typography variant="h5" sx={{ mb: 2 }}>
        교육 프로그램 관리
      </Typography>

      <Button variant="contained" startIcon={<Add />} onClick={() => handleOpen()}>
        프로그램 추가
      </Button>

      <Paper sx={{ mt: 3 }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell width="80">번호</TableCell>
              <TableCell>제목</TableCell>
              <TableCell align="right">관리</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {programs.map((item) => (
              <TableRow key={item.id}>
                <TableCell>{item.id}</TableCell>
                <TableCell>{item.title}</TableCell>
                <TableCell align="right">
                  <IconButton onClick={() => handleOpen(item)}><Edit /></IconButton>
                  <IconButton onClick={() => handleDelete(item.id)}><Delete color="error" /></IconButton>
                </TableCell>
              </TableRow>
            ))}
            {programs.length === 0 && (
              <TableRow>
                <TableCell colSpan={3} align="center">데이터가 없습니다.</TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </Paper>

      {/* 팝업 Dialog */}
      <Dialog
        open={open}
        onClose={handleClose}
        fullWidth
        maxWidth="md"
      >
        <DialogTitle>{editItem ? '프로그램 수정' : '프로그램 추가'}</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="제목"
            fullWidth
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />

          <Typography variant="subtitle1" sx={{ mt: 2, mb: 1 }}>내용</Typography>
          <ReactQuill theme="snow" value={info} onChange={setInfo} style={{ height: '200px' }} />
        </DialogContent>
        <DialogActions>
          <Button variant="contained" onClick={handleSave}>
            {editItem ? '수정' : '추가'}
          </Button>
          <Button onClick={handleClose}>취소</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}

export default Program;
