import React from 'react';
import { useLocation, useNavigate, Routes, Route } from 'react-router-dom';

import { AppBar, Tabs, Tab, Box, Container, Paper } from '@mui/material';

import History from './History';
import Program from './Program';

function AdminLayout() {
 const location = useLocation();
 const navigate = useNavigate();

 // 현재 탭 index 계산
 const path = location.pathname;
 const tabIndex = path.includes('program') ? 1 : 0;

 const handleTabChange = (_, newValue) => {
  if (newValue === 0) navigate('/admin/dashboard/history');     // ✅ 절대 경로
  else if (newValue === 1) navigate('/admin/dashboard/program'); // ✅ 절대 경로
 };


 return (
  <Box sx={{ bgcolor: '#f5f6f8', minHeight: '100vh' }}>
   <AppBar position="static" color="default" elevation={1}>
     <Tabs value={tabIndex} onChange={handleTabChange} centered>
       <Tab label="연혁" />
       <Tab label="교육 프로그램" />
     </Tabs>
   </AppBar>

   <Container maxWidth="md" sx={{ mt: 4 }}>
     <Paper elevation={3} sx={{ p: 4, borderRadius: 2 }}>
       <Routes>
         <Route path="history" element={<History />} />
         <Route path="program" element={<Program />} />
       </Routes>
     </Paper>
   </Container>
 </Box>
 );
}

export default AdminLayout;
